function [Wp,Lp,Fi,Wf,Gpf,Lg, Wg] = antenna1(H,Er,Fr,Z0)%%  h,er,f,z -> wp, lp, fi, wf, lf, gpf, wg, lg%       h (mm)%       er (constante)%       f (GHZ)%       wf, wg, wp (mm)%       lg, lp (mm)%       z (ohms)%%Fr=Fr*1e9;%calculate the patch's witdth Wp=(3e8/(sqrt((Er+1)/2)*2*Fr))*1000;        %in mm%calculate the effective dielectric constante_eff=((Er+1)/2)+ (((Er-1)/2)* (1+((12*H)/Wp))^-0.5);%calculate the extension of length lpdel_l=(((e_eff+0.3)*((Wp/H)+0.264))/((e_eff-0.258)*((Wp/H)+0.8)))*(0.412*H);  %in mm%calculate the effective lengthl_eff=(3e8/(2*Fr*sqrt(e_eff)))*1000;%calculate the length's patchLp=l_eff-(2*del_l);la=(3e8/Fr)*1000;k=(2*pi)/la;x=k*(Wp);i1=-2+cos(x)+(x*sinint(x))+(sin(x)/x);g1=i1/(120*pi*pi);          %Conductancea=@(th)(((sin((x./2).*cos(th))./cos(th)).^2).*(besselj(0,(k.*Lp.*sin(th)))).*(sin(th)).^3);a1=integral(a,0,pi);g12=a1/(120*pi*pi);     %in siemensr_in=1/(2*(g1+g12));    %in ohms%calculate the inset feed pointFi=(Lp/pi)*(acos(sqrt(Z0/r_in)));%calculate the min of width's and length's ground planeLg=6*H+Lp;   %based on Punit S. Nakar (2004) bookWg=6*H+Wp;B=60*pi*pi/(Z0*sqrt(Er)); m1=2*B-1;m=log(m1);n1=B-1;n=log(n1);%calculate the width's feed lineWf=(2*H/pi)*(B-1-m+(((Er-1)/(2*Er))*(n+(0.39*0.61)/Er)));   %based on Liang J. (2004) book Gpf=(3e8*4.65e-9)/(sqrt(2*e_eff)*Fr*10^-9);    end